import nextConnect from 'next-connect';
import db from '../../lib/db';

const handler = nextConnect();

handler.use(db);

handler.get(async (req, res) => {
    let doc = await req.db.collection('listingsAndReviews').findOne()
    console.log(doc);
    res.json(doc);
});

export default handler;
